"use strict";var waitForTransactionReceipt=function waitForTransactionReceipt(tronWeb){return function(){var txHash=arguments.length>0&&arguments[0]!==undefined?arguments[0]:false;var interval=arguments.length>1&&arguments[1]!==undefined?arguments[1]:500;var transactionReceiptAsync=function transactionReceiptAsync(resolve,reject){tronWeb.trx.getTransactionInfo(txHash,function(error,receipt){if(error){reject(error)}else if(!receipt||JSON.stringify(receipt)==="{}"){setTimeout(function(){return transactionReceiptAsync(resolve,reject)},interval)}else{resolve(receipt)}})};if(Array.isArray(txHash)){return Promise.all(txHash.map(function(oneTxHash){return waitForTransactionReceipt(oneTxHash,interval)}))}else if(typeof txHash==="string"){return new Promise(transactionReceiptAsync)}else{throw new Error("Invalid Type: "+txHash)}}};module.exports=waitForTransactionReceipt;